import React, { useEffect, useState } from "react";

function Historico() {
    const [historico, setHistorico] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch('http://localhost:8000/beer_budies/api/minhas_bebidas/', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('access')}`
            }
        })
        .then(res => res.json())
        .then(data => {
            if (Array.isArray(data)) {
                setHistorico(data.filter(ev => ev.cervejas && ev.cervejas > 0));
            }
            setLoading(false);
        })
        .catch(() => setLoading(false));
    }, []);

    if (loading) return <div>A carregar histórico...</div>;

    if (historico.length === 0) return <div>Sem eventos registados.</div>;

    return (
        <div>
            <hr />
            <h2>Histórico de Eventos</h2>
            <ul style={{ listStyle: "none", padding: 0 }}>
                {historico.map((ev, idx) => (
                    <li key={ev.id || idx} style={{
                        marginBottom: "1.2em",
                        padding: "1em",
                        border: "1px solid #FA9020",
                        borderRadius: "8px",
                        background: "#fffbe6"
                    }}>
                        {ev.evento && <div style={{ fontWeight: "bold", fontSize: "1.2em", color: "#FA9020" }}>{ev.evento}</div>}
                        <div><b>Local:</b> {ev.local || "Desconhecido"}</div>
                        <div><b>Cervejas:</b> {ev.cervejas}</div>
                        {ev.data && (
                            <div>
                                <b>Data:</b> {new Date(ev.data).toLocaleDateString()} {new Date(ev.data).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                            </div>
                        )}
                        <div><b>Coordenadas:</b> {ev.coordenadas}</div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Historico;
