import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../Header/Header';
import LogIn from './LogIn.js';
import axios from 'axios';
import gruposData from '../Cards/Grupos.json'
import '../cssFiles/Grupos.css';


const Grupos = () => {
  const navigate = useNavigate(); 
  const [grupos, setGrupos] = useState([]);
  const ENDPOINT_URL = 'http://127.0.0.1:8000/beer_budies/api/grupos';

  useEffect( () => { axios.get(ENDPOINT_URL).then(response => setQuestions(response.data));}, [] );

  return (
    <div className="app-container">
      <Header />
      <h2 style={{ textAlign: 'center', marginTop: '20px' }}>Grupos Disponíveis</h2>
      <div className="lista-grupos">
        {gruposData.map((grupo) => (
          <button
            key={grupo.nome}
            className="card card-button"
            onClick={() => navigate('/login')} // Updated to match the correct route
          >
            <h3>{grupo.nome}</h3>
            <p><strong>Faculdade:</strong> {grupo.faculdade}</p>
            <p><strong>Número de Seguidores:</strong> {grupo.num_membros}</p>
          </button>
        ))}
      </div>

    </div>
  );
};

export default Grupos;
