import React, {useEffect, useState } from 'react';
import { useNavigate} from 'react-router-dom';
import axios from 'axios';
import '../cssFiles/Grupos.css';
import Header from '../Header/Header';



const CriarGrupo = () => {
  const navigate = useNavigate(); 
  const [nomeE, setnomeE] = useState("");
  const [uniE, setuniE] = useState("");
  const [dataE, setdataE] = useState("");
  const [horaE, sethoraE] = useState("");
  const [precoJolaE, setprecoJolaE] = useState("");

  const ENDPOINT_URL = 'http://127.0.0.1:8000/beer_budies/api/grupos/';

  const submitHandler = (e) => {
     e.preventDefault();

    if (nomeE.trim() === "") {
        document.getElementById("warning_message").innerText="É obrigatório tem nome!";
        return;
    }

    axios.post(ENDPOINT_URL, {"nome": nomeE, "universidade":uniE,"data":dataE,"hora":horaE,"preco_J":precoJolaE}).then();}
    navigate('/')





  return (

    <div className="app-container">
        <Header />
        <div className='form_container'>
            <h1>Inserir novo Evento</h1>
            <form onSubmit={submitHandler}>
                <label className="form-label">Nome do Evento: </label>
                <input className="form-input" type="text" value={nomeE} onChange={(e) => setnomeE(e.target.value)}/>
                <br/><br/>
                <label className="form-label">Universidade: </label>
                <input className="form-input"  value={uniE} onChange={(e) => setuniE(e.target.value)} rows={4} style={{resize: 'vertical'}} />

                <label className="form-label">Data: </label>
                <input className="form-input" type="date" value={dataE} onChange={(e) => setdataE(e.target.value)}/>
                <label className="form-label">Hora: </label>
                <input className="form-input" type="time" value={horaE} onChange={(e) => sethoraE(e.target.value)}/>
                <label className="form-label">Preço Cerveja: </label>
                <input className="form-input" type="number" value={precoJolaE} onChange={(e) => setprecoJolaE(e.target.value)}/>€
                <p id="warning_message"></p>
                <br/><br/>
                <input className="form-submit" type="submit" value="Submeter"/>
            </form>
        </div>

    </div>
  );
};

export default CriarGrupo;
