import React, { useState } from 'react';
import Header from '../Header/Header';
import Mapa from './mapa';
import ProximosEventos from './ProximosEventos';
import Calculadora from './Calculadora';
import Footer from './Footer';
import Historico from './Historico'; // importa o Historico

const Home = () => {
  const [refreshHeader, setRefreshHeader] = useState(0);

  // Função para atualizar o Header
  const atualizarHeader = () => setRefreshHeader(v => v + 1);

  return (
    <div className='app-container' style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <Header refreshTrigger={refreshHeader} />
      <div style={{ flex: 1 }}>
        <Mapa onBebidaAdicionada={atualizarHeader} />
        <Historico onAtualizarContador={atualizarHeader} /> {/* Adiciona isto */}
      </div>
      <ProximosEventos />
      <Calculadora />
      <Footer />
    </div>
  );
};

export default Home;
